mapper.select:
SELECT
a.violationWeight,
a.Packets,
b.trashType
FROM t_trash_collect a left join t_trash_type b on a.trashId = b.trashId


        AND a.createDate >=#{startTime}


        AND a.createDate <=#{endTime}


        AND b.trashType = #{trashType}


ORDER BY a.createDate DESC

SELECT
COUNT(warnDamagedId)
FROM t_warn_damaged a


        AND a.createDate >=#{startTime}


        AND a.createDate <=#{endTime}

SELECT
COUNT(warnInWeightId)
FROM t_warn_inWeight a


        AND a.createDate >=#{startTime}


        AND a.createDate <=#{endTime}

SELECT
COUNT(warnLeakageId)
FROM t_warn_leakage a


        AND a.createDate >=#{startTime}


        AND a.createDate <=#{endTime}

SELECT
COUNT(warnLoseId)
FROM t_warn_lose a


        AND a.createDate >=#{startTime}


        AND a.createDate <=#{endTime}

SELECT
COUNT(warnNoOutId)
FROM t_warn_noOut a


        AND a.createDate >=#{startTime}


        AND a.createDate <=#{endTime}

SELECT
COUNT(warnOutOvertimeId)
FROM t_warn_outOvertime a


        AND a.createDate >=#{startTime}


        AND a.createDate <=#{endTime}

SELECT
COUNT(warnOutWeightId)
FROM t_warn_outWeight a


        AND a.createDate >=#{startTime}


        AND a.createDate <=#{endTime}

SELECT
COUNT(warnViolationId)
FROM t_warn_violation a


        AND a.createDate >=#{startTime}


        AND a.createDate <=#{endTime}
