mapper.select:
SELECT 

FROM t_nurse a
WHERE a.nurseId = #{id}

SELECT

FROM t_nurse a


		AND a.nurseName like concat('%',#{nurseName},'%')


		AND a.nurseCode = #{nurseCode}


		AND a.departmentId = #{departmentId}


		AND a.officeId = #{officeId}


ORDER BY a.createDate DESC

SELECT

FROM t_nurse a
WHERE a.nurseName = #{value}
