mapper.select:
SELECT

FROM t_trash_collect a
WHERE a.trashCollectId = #{id}

SELECT

FROM t_trash_collect a
WHERE a.trashCollectName = #{value}

SELECT

FROM t_trash_collect a


		AND a.code = #{code}


		AND a.trashId = #{trashId}


		AND a.departmentId = #{departmentId}


		AND a.officeId = #{officeId}


		AND a.collectNumber = #{collectNumber}


		   and DATE_FORMAT(a.writeTime, '%Y-%m-%d %H:%i:%s') >=  DATE_FORMAT(#{startTime}, '%Y-%m-%d %H:%i:%s')   


		  and DATE_FORMAT(a.writeTime, '%Y-%m-%d %H:%i:%s') <= DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%s')
